import React, { useState, useEffect } from "react";
// import image from '../image/puritemple.jpg'
import smll from '../image/odisha-sc.jpg'
import anugul from '../image/ANUGUL.jpg'
import Balangir from '../image/Balangir.jpg'
import Balasore from '../image/Balasore.jpg'
import Bargarh from '../image/Bargarh.jpg'
import Bhadrak from '../image/Bhadrak.jpg'
import Boudh from '../image/Boudh.jpg';
import cuttack from '../image/Cuttack.JPG';
import Debagarh from '../image/Debagarh.jpg';
import Dhenkanal from '../image/Dhenkanal.jpg';
import Gajapati from '../image/Gajapati.jpg';
import Ganjam from '../image/ganjam.jpg';
import Jagatsinghpur from '../image/Jagatsinghpur.jpg';
import Jajpur from '../image/Dashashwamedh-Ghat-Jajpur.jpg';
import Jharsuguda from '../image/Jharsuguda.jpg';
import Kalahandi from '../image/Kalahandi.jpeg';
import Kandhamal from '../image/Kandhamal.jpg';
import Kendrapara from '../image/Kendrapara.jpg';
import Khordha from '../image/Khordha.jpg';
import Koraput from '../image/Koraput.jpg';
import Malkangiri from '../image/Malkangiri.jpg';
import Mayurbhanj from '../image/Mayurbhanj.jpg';
import Nabarangpur from '../image/Nabarangpur.png';
import Nayagarh from '../image/Nayagarh.jpg';
import Nuapada from '../image/Nuapada.jpg';
import Puri from '../image/Puri.jpg';
import Rayagada from '../image/Rayagada.jpg';
import Sambalpur from '../image/Sambalpur.jpg';
import Subarnapur from '../image/Subarnapur.jpg';
import Sundergarh from '../image/Sundergarh.jpg';
import { Slide } from "react-awesome-reveal";
import PulseLoader from "react-spinners/PulseLoader";



const About = () => {




  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    function handleScroll() {
      if (window.scrollY > 300) {
        setShowButton(true);
      } else {
        setShowButton(false);
      }
    }

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  function scrollToTop() {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }



  const [loaderStatus, setLoaderStatus] = useState(true);
  useEffect(() => {
    setTimeout(() => {
      setLoaderStatus(false);
    }, 2000);
  }, []);



  return (
    <div className="bg-gray-100 py-10">
    <div className="container mx-auto px-6 md:px-12 lg:px-20">
      {/* Introduction Section */}
      <section className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-800">About TourMate</h1>
        <p className="mt-4 text-gray-600 text-lg">
          TourMate is a cutting-edge platform designed to revolutionize the tourism experience in Nepal. 
          Our goal is to provide international tourists with seamless travel planning, booking, and support 
          services through a centralized, user-friendly platform.
        </p>
      </section>

      {/* Mission Statement */}
      <section className="mb-12">
        <h2 className="text-3xl font-semibold text-gray-800 text-center">Our Mission</h2>
        <p className="mt-4 text-gray-600 text-lg text-center">
          At TourMate, our mission is to bridge the gap between travelers and travel services, offering a streamlined 
          experience with personalized itineraries, easy bookings, and real-time user assistance to make every journey 
          in Nepal unforgettable.
        </p>
      </section>

      {/* Core Values */}
      <section className="mb-12">
        <h2 className="text-3xl font-semibold text-gray-800 text-center">Our Core Values</h2>
        <ul className="mt-6 space-y-4 text-gray-600 text-lg">
          <li>🌍 <strong>Customer-Centric Experience</strong> - We put travelers first, ensuring that every aspect of their journey is smooth and enjoyable.</li>
          <li>🧭 <strong>Innovation</strong> - We continually innovate to provide the best solutions for trip planning, navigation, and support.</li>
          <li>🌟 <strong>Quality Service</strong> - We believe in maintaining consistent quality, offering clear and precise information to our users.</li>
          <li>🌤 <strong>Responsiveness</strong> - Our platform is built to provide timely support with real-time assistance and weather updates.</li>
        </ul>
      </section>

      {/* What Sets Us Apart */}
      <section className="mb-12">
        <h2 className="text-3xl font-semibold text-gray-800 text-center">What Sets Us Apart</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-6">
          <div>
            <h3 className="text-2xl font-semibold text-gray-800">Comprehensive Itineraries</h3>
            <p className="mt-2 text-gray-600 text-lg">
              TourMate provides personalized itineraries based on user preferences, with detailed roadmaps and seamless booking integration.
            </p>
          </div>
          <div>
            <h3 className="text-2xl font-semibold text-gray-800">Real-Time Support</h3>
            <p className="mt-2 text-gray-600 text-lg">
              Our platform ensures you receive responsive user support throughout your journey, enhancing your overall experience.
            </p>
          </div>
          <div>
            <h3 className="text-2xl font-semibold text-gray-800">Enhanced Navigation</h3>
            <p className="mt-2 text-gray-600 text-lg">
              With integrated mapping and navigation tools, we help you explore Nepal with confidence and ease.
            </p>
          </div>
          <div>
            <h3 className="text-2xl font-semibold text-gray-800">Weather Information</h3>
            <p className="mt-2 text-gray-600 text-lg">
              Stay updated with real-time weather information to ensure safe and enjoyable travel experiences.
            </p>
          </div>
        </div>
      </section>

      {/* Team Information */}
      <section>
        <h2 className="text-3xl font-semibold text-gray-800 text-center">Meet Our Team</h2>
        <p className="mt-4 text-gray-600 text-lg text-center">
          TourMate is developed by a dedicated team of travel enthusiasts and tech experts who are passionate about 
          creating an unforgettable travel experience in Nepal. We are committed to helping you explore the beauty and 
          culture of Nepal with ease.
        </p>
      </section>
    </div>
  </div>
  )
}

export default About
